import React, { useRef, useState, useEffect } from "react";
import { skills } from "../data"; // Ensure this includes the icon data
import {
  Box,
  Container,
  Heading,
  Text,
  Icon,
  useColorMode,
  Card,
  CardBody,
  CardHeader,
} from "@chakra-ui/react";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css"; // Import Carousel styles
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"; // Import FontAwesomeIcon
import { faArrowLeft, faArrowRight } from "@fortawesome/free-solid-svg-icons"; // Import arrow icons

export default function Skills() {
  const { colorMode } = useColorMode();
  const carouselRef = useRef(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isUserInteracting, setIsUserInteracting] = useState(false);

  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5,
      partialVisibilityGutter: 20,
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
      partialVisibilityGutter: 20,
    },
    tablet: {
      breakpoint: { max: 1024, min: 768 },
      items: 2,
      partialVisibilityGutter: 15,
    },
    mobile: {
      breakpoint: { max: 768, min: 0 },
      items: 1,
      partialVisibilityGutter: 10,
    },
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      if (!isUserInteracting) {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % skills.length);
        carouselRef.current.goToSlide(currentIndex, 500);
      }
    }, 5000);
    return () => clearInterval(intervalId);
  }, [currentIndex, isUserInteracting, skills.length]);

  const handleArrowClick = (direction) => {
    setIsUserInteracting(true);
    if (direction === "next") {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % skills.length);
      carouselRef.current.next(500);
    } else {
      setCurrentIndex(
        (prevIndex) => (prevIndex - 1 + skills.length) % skills.length
      );
      carouselRef.current.previous(500);
    }
  };

  return (
    <Box
      id="skills"
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={{ base: 10, md: 20 }} // Responsive padding
      borderBottom="1px solid"
      borderBottomColor={colorMode === "light" ? "gray.200" : "gray.700"}
      boxShadow="0px 10px 20px rgba(0, 0, 0, 0.1)"
    >
      <Container
        maxW="container.xl"
        px={{ base: 4, md: 10 }} // Responsive padding
        mx="auto"
        textAlign="center"
      >
        <Heading
          as="h1"
          fontSize={{ base: "2xl", md: "4xl" }} // Responsive font sizes
          mb={4}
          color={colorMode === "light" ? "gray.900" : "white"}
        >
          My Graphic Design Skills
        </Heading>
        <Text
          fontSize={{ base: "sm", md: "lg" }} // Responsive font sizes
          mb={8}
          color={colorMode === "light" ? "gray.600" : "gray.400"}
        >
          As a graphics designer, I've acquired a range of skills that enable me
          to create visually appealing designs. From logos to brochures, I've
          got you covered.
        </Text>

        <Box w="full" overflow="hidden">
          <Carousel
            maxWidth="100vw"
            maxHeight="100vh"
            padding={{ base: 2, md: 4 }}
            margin={{ base: 1, md: 2 }}
            ref={carouselRef}
            responsive={responsive}
            containerClass="carousel-container"
            additionalTransfrom={0}
            arrows={false}
            itemClass="carousel-item"
            slidesToSlide={1}
            swipeable={true}
            draggable={true}
            focusOnSelect={true}
          >
            {skills.map((skill) => (
              <Card
                key={skill.name}
                bg={colorMode === "light" ? "gray.200" : "gray.800"}
                color={colorMode === "light" ? "gray.600" : "gray.400"}
                p={4}
                border="1px solid"
                borderColor={colorMode === "light" ? "gray.200" : "gray.800"}
                borderRadius="lg"
                mx={1}
                mb={4}
                _hover={{
                  bg: colorMode === "light" ? "gray.300" : "gray.700",
                  color: "white",
                  transform: "scale(1.05)",
                  transition: "all 0.3s ease-in-out",
                }}
                display="flex"
                flexDirection="column"
                justifyContent="center"
              >
                <CardHeader>
                  <Icon w={12} h={12} as={FontAwesomeIcon} icon={skill.icon} />
                </CardHeader>
                <CardBody>
                  <Text fontSize="lg" fontWeight="bold">
                    {skill.name}
                  </Text>
                </CardBody>
              </Card>
            ))}
          </Carousel>
        </Box>

        <Box mt={4} display="flex" justifyContent="center" gap={4}>
          <button onClick={() => handleArrowClick("prev")}>
            <FontAwesomeIcon icon={faArrowLeft} size="2x" />
          </button>
          <button onClick={() => handleArrowClick("next")}>
            <FontAwesomeIcon icon={faArrowRight} size="2x" />
          </button>
        </Box>
      </Container>
    </Box>
  );
}
