import { CodeIcon } from "@heroicons/react/solid";
import React from "react";
import { projects } from "../data";
import {
  Box,
  Container,
  Grid,
  GridItem,
  Heading,
  Text,
  Link,
  Image,
  useColorMode,
} from "@chakra-ui/react";

export default function Projects() {
  const { colorMode } = useColorMode();

  return (
    <Box
      id="projects"
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={20}
    >
      <Container
        maxW="container.xl"
        mx="auto"
        px={{ base: 4, md: 10 }}
        textAlign="center"
      >
        <Grid justify="center" align="center" mb={8}>
          <CodeIcon className="inline-block w-6 h-6 mb-4" height={100} />
          <Heading
            as="h1"
            fontSize={{ base: "2xl", md: "3xl", lg: "4xl" }}
            mb={4}
            color={colorMode === "light" ? "gray.900" : "white"}
          >
            Full Stack Web Development Projects
          </Heading>
          <Text
            fontSize={{ base: "md", md: "lg" }}
            mb={8}
            color={colorMode === "light" ? "gray.600" : "gray.400"}
          >
            As a full stack web developer, I've built a variety of projects that
            showcase my skills in front-end and back-end development. From
            responsive web design to API integration, I've got you covered.
          </Text>
        </Grid>
        <Grid
          templateColumns={{ base: "repeat(1, 1fr)", md: "repeat(2, 1fr)" }}
          gap={8}
        >
          {projects.map((project) => (
            <GridItem key={project.image}>
              <Link href={project.link}>
                <Box
                  bg={colorMode === "light" ? "gray.200" : "gray.800"}
                  color={colorMode === "light" ? "gray.600" : "gray.400"}
                  p={4}
                  border="1px solid"
                  borderColor={colorMode === "light" ? "gray.200" : "gray.800"}
                  borderRadius="lg"
                  boxShadow="lg"
                  _hover={{
                    position: "relative",
                    overflow: "hidden",
                  }}
                >
                  <Box position="relative" overflow="hidden" borderRadius="lg">
                    <Image
                      src={project.image}
                      alt="project image"
                      w="full"
                      h={{ base: "150px", md: "200px" }}
                      objectFit="cover"
                      objectPosition="center"
                      borderRadius="lg"
                      _hover={{
                        transform: "scale(1.1)",
                        transition: "transform 0.3s ease-in-out",
                      }}
                    />
                    <Box
                      position="absolute"
                      top="0"
                      left="0"
                      w="full"
                      h="full"
                      bg={colorMode === "light" ? "gray.100" : "gray.900"}
                      opacity="0"
                      _hover={{
                        opacity: "0.8",
                        transition: "opacity 0.3s ease-in-out",
                      }}
                      display="flex"
                      flexDirection="column"
                      justifyContent="center"
                      alignItems="center"
                      borderRadius="lg"
                    >
                      <Heading
                        as="h2"
                        fontSize={{ base: "md", md: "lg" }}
                        mb={2}
                        color={colorMode === "light" ? "gray.900" : "white"}
                      >
                        {project.title}
                      </Heading>
                      <Text
                        fontSize={{ base: "sm", md: "md" }}
                        mb={2}
                        color={colorMode === "light" ? "gray.600" : "gray.400"}
                      >
                        {project.subtitle}
                      </Text>
                      <Text
                        fontSize={{ base: "sm", md: "md" }}
                        color={colorMode === "light" ? "gray.600" : "gray.400"}
                      >
                        {project.description}
                      </Text>
                    </Box>
                  </Box>
                </Box>
              </Link>
            </GridItem>
          ))}
        </Grid>
      </Container>
    </Box>
  );
}
