import { CodeIcon } from "@heroicons/react/solid";
import React from "react";
import { projects } from "../data";
import {
  Box,
  Container,
  Grid,
  GridItem,
  Heading,
  Text,
  Link,
  Image,
  useColorMode,
} from "@chakra-ui/react";

export default function Projects() {
  const { colorMode } = useColorMode();

  return (
    <Box
      id="projects"
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={10} // Decrease the margin top
    >
      <Container
        maxW="container.xl"
        mx="auto"
        px={{ base: 4, md: 10 }}
        textAlign="center"
      >
        <Grid justify="center" align="center" mb={8}>
          <Heading
            as="h1"
            fontSize={{ base: "2xl", md: "3xl", lg: "4xl" }}
            mb={4}
            color={colorMode === "light" ? "gray.900" : "white"}
          >
            Graphics Design
          </Heading>
          <Text
            fontSize={{ base: "md", md: "lg" }}
            mb={8}
            color={colorMode === "light" ? "gray.600" : "gray.400"}
          >
            As a graphics designer, I've created a variety of projects that
            showcase my skills in visual design and creativity. From logos to
            brochures, I've got you covered.
          </Text>
        </Grid>
        <Grid
          templateColumns={{ base: "repeat(1, 1fr)", md: "repeat(3, 1fr)" }}
          gap={8}
        >
          {projects.map((project) => (
            <GridItem key={project.title}>
              <Box
                bg={colorMode === "light" ? "gray.200" : "gray.800"}
                color={colorMode === "light" ? "gray.600" : "gray.400"}
                p={4}
                border="1px solid"
                borderColor={colorMode === "light" ? "gray.200" : "gray.800"}
                borderRadius="lg"
                boxShadow="lg"
                _hover={{
                  position: "relative",
                  overflow: "hidden",
                }}
                w="full"
                h={{ base: "250px", md: "300px" }} // Add a fixed height for the cards
              >
                <Image
                  src={project.image} // Use project.image instead of thumbnail4
                  alt={project.title}
                  borderRadius="lg"
                  objectFit="cover"
                  boxSize="100%" // Fill the Box
                />
                <Box
                  position="absolute"
                  top={0}
                  left={0}
                  w="full"
                  h="full"
                  bg="rgba(0, 0, 0, 0.5)" // Add a semi-transparent background
                  display="none" // Hide the overlay by default
                  _hover={{
                    display: "block", // Show the overlay on hover
                  }}
                >
                  <Text
                    fontSize="lg"
                    fontWeight="bold"
                    color="white"
                    mt={4}
                    ml={4}
                  >
                    {project.title}
                  </Text>
                  <Text fontSize="md" color="white" mt={2} ml={4}>
                    {project.subtitle}
                  </Text>
                </Box>
              </Box>
            </GridItem>
          ))}
        </Grid>
      </Container>
    </Box>
  );
}
