import {
  Box,
  Flex,
  Link,
  Button,
  IconButton,
  Image,
  useColorMode,
  useMediaQuery,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
} from "@chakra-ui/react";
import { HamburgerIcon, CloseIcon } from "@chakra-ui/icons";
import { useState, useRef, useEffect } from "react";
import { motion } from "framer-motion";

const Navbar = () => {
  const { colorMode, toggleColorMode } = useColorMode();
  const [isOpen, setIsOpen] = useState(false);
  const [isLargerThan768] = useMediaQuery("(min-width: 768px)");
  const hasMountedRef = useRef(false);

  useEffect(() => {
    hasMountedRef.current = true;
  }, []);

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <Box
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      pt={4}
      pb={2}
      sticky
      top={0}
      zIndex={10}
      transition="background-color 0.3s ease"
    >
      <Flex
        justify="space-between"
        align="center"
        flexWrap="wrap"
        px={{ base: 4, md: 10 }}
      >
        <Box>
          <Link href="#about" target="_blank" rel="noopener noreferrer">
            <motion.div
              initial={{ rotate: 0 }}
              animate={hasMountedRef.current ? {} : { rotate: 360 }}
              transition={
                hasMountedRef.current ? {} : { duration: 1, repeat: 1 }
              }
            >
              <Image src={require("../logo.png")} alt="Logo" width="70px" />
            </motion.div>
          </Link>
        </Box>
        {isLargerThan768 ? (
          <Flex justify="space-between" align="center">
            <Link
              href="#projects"
              fontSize="lg"
              color="gray.400"
              _hover={{ color: "white" }}
              mr={5}
              target="_blank"
              rel="noopener noreferrer"
            >
              Past Work
            </Link>
            <Link
              href="#testimonials"
              fontSize="lg"
              color="gray.400"
              _hover={{ color: "white" }}
              mr={5}
              target="_blank"
              rel="noopener noreferrer"
            >
              Testimonials
            </Link>
            <Button
              as={Link}
              href="#contact"
              variant="solid"
              colorScheme="green"
              size="lg"
              target="_blank"
              rel="noopener noreferrer"
            >
              Hire Me
            </Button>
          </Flex>
        ) : (
          <Menu isOpen={isOpen}>
            <MenuButton
              as={IconButton}
              aria-label="Open menu"
              icon={isOpen ? <CloseIcon /> : <HamburgerIcon />}
              onClick={toggleMenu}
              colorScheme="gray"
            />
            <MenuList>
              <MenuItem
                as={Link}
                href="#projects"
                target="_blank"
                rel="noopener noreferrer"
              >
                Past Work
              </MenuItem>
              <MenuItem
                as={Link}
                href="#testimonials"
                target="_blank"
                rel="noopener noreferrer"
              >
                Testimonials
              </MenuItem>
              <MenuItem
                as={Link}
                href="#contact"
                target="_blank"
                rel="noopener noreferrer"
              >
                Hire Me
              </MenuItem>
            </MenuList>
          </Menu>
        )}
      </Flex>
    </Box>
  );
};

export default Navbar;
