import React, { useRef, useState, useEffect } from "react";
import { skills } from "../data"; // Ensure this includes the icon data
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css"; // Import Carousel styles
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"; // Import FontAwesomeIcon

export default function Skills() {
  const carouselRef = useRef(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5,
      partialVisibilityGutter: 40,
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
      partialVisibilityGutter: 30,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
      partialVisibilityGutter: 20,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
      partialVisibilityGutter: 10,
    },
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex((currentIndex + 1) % skills.length);
      carouselRef.current.goToSlide(currentIndex, 500);
    }, 5000);
    return () => clearInterval(intervalId);
  }, [currentIndex, skills]);

  const handleArrowClick = (direction) => {
    if (direction === "next") {
      setCurrentIndex((currentIndex + 1) % skills.length);
      carouselRef.current.next(500);
    } else {
      setCurrentIndex((currentIndex - 1 + skills.length) % skills.length);
      carouselRef.current.previous(500);
    }
  };

  return (
    <div style={{ padding: "20px", textAlign: "center" }}>
      <h1 style={{ fontSize: "2rem", marginBottom: "10px" }}>
        My Graphic Design Skills
      </h1>
      <p style={{ marginBottom: "20px" }}>
        As a graphics designer, I've acquired a range of skills that enable me
        to create visually appealing designs. From logos to brochures, I've got
        you covered.
      </p>

      <div style={{ overflow: "hidden" }}>
        <Carousel
          ref={carouselRef}
          responsive={responsive}
          containerClass="carousel-container"
          additionalTransfrom={0}
          arrows={true} // Enable arrows for navigation
          itemClass="carousel-item"
          slidesToSlide={1}
          swipeable={true}
          draggable={true}
          focusOnSelect={true}
          infinite={true} // Ensure infinite looping
        >
          {skills.map((skill) => (
            <div
              key={skill.name}
              style={{
                margin: "0 10px",
                display: "flex",
                justifyContent: "center",
              }}
            >
              <div
                style={{
                  background: "#f0f0f0",
                  border: "1px solid #ccc",
                  borderRadius: "10px",
                  padding: "20px",
                  textAlign: "center",
                  transition: "transform 0.3s",
                  cursor: "pointer",
                }}
                onMouseOver={(e) =>
                  (e.currentTarget.style.transform = "scale(1.05)")
                }
                onMouseOut={(e) =>
                  (e.currentTarget.style.transform = "scale(1)")
                }
              >
                <FontAwesomeIcon icon={skill.icon} size="3x" />
                <h3 style={{ marginTop: "10px" }}>{skill.name}</h3>
                <p>{skill.description}</p>
              </div>
            </div>
          ))}
        </Carousel>
      </div>
    </div>
  );
}
