import {
  Box,
  Container,
  Flex,
  VStack,
  Button,
  theme as chakraTheme,
  IconButton,
  useColorMode,
} from "@chakra-ui/react";
import { FaMoon, FaArrowUp, FaArrowDown } from "react-icons/fa";
import { BsSun } from "react-icons/bs";
import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import theme from "./theme";
import Home from "./components/Home";

import About from "./components/About";
import Contact from "./components/Contact";
import Navbar from "./components/Navbar";
import Projects from "./components/Projects";
import Skill from "./components/Skill";
import Testimonies from "./components/Testimonies";
import Footer from "./components/Footer";

function App() {
  const { colorMode = "light", toggleColorMode } = useColorMode();
  const [isToggleFixed, setIsToggleFixed] = useState(false);
  const [showScrollToTop, setShowScrollToTop] = useState(false);
  const [showScrollToBottom, setShowScrollToBottom] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsToggleFixed(window.pageYOffset > 100);
      setShowScrollToTop(window.pageYOffset > 200);
      setShowScrollToBottom(
        window.pageYOffset + window.innerHeight >= document.body.scrollHeight
      );
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleScrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  const handleScrollToBottom = () => {
    window.scrollTo({
      top: document.body.scrollHeight,
      behavior: "smooth",
    });
  };
  return (
    <ChakraProvider theme={theme}>
      <BrowserRouter>
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
        <Footer />
      </BrowserRouter>
    </ChakraProvider>
  );
}

export default App;
