import React, { useRef, useState, useEffect } from "react";
import { skills } from "../data"; // Ensure this includes the icon data
import {
  Box,
  Container,
  Heading,
  Text,
  Icon,
  useColorMode,
  Card,
  CardBody,
  CardHeader,
} from "@chakra-ui/react";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css"; // Import Carousel styles
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"; // Import FontAwesomeIcon

export default function Skills() {
  const { colorMode } = useColorMode();
  const carouselRef = useRef(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5,
      partialVisibilityGutter: 0, // Set to 0
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
      partialVisibilityGutter: 0, // Set to 0
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
      partialVisibilityGutter: 0, // Set to 0
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
      partialVisibilityGutter: 0, // Set to 0
    },
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex((currentIndex + 1) % skills.length);
      carouselRef.current.goToSlide(currentIndex, 500);
    }, 5000);
    return () => clearInterval(intervalId);
  }, [currentIndex, skills]);

  const handleArrowClick = (direction) => {
    if (direction === "next") {
      setCurrentIndex((currentIndex + 1) % skills.length);
      carouselRef.current.next(500);
    } else {
      setCurrentIndex((currentIndex - 1 + skills.length) % skills.length);
      carouselRef.current.previous(500);
    }
  };

  return (
    <Box
      id="skills"
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={{ base: 10, md: 20 }} // Responsive padding
      borderBottom="1px solid"
      borderBottomColor={colorMode === "light" ? "gray.200" : "gray.700"}
      boxShadow="0px 10px 20px rgba(0, 0, 0, 0.1)"
    >
      <Container
        maxW="container.xl"
        px={{ base: 4, md: 10 }} // Responsive padding
        mx="auto"
        textAlign="center"
      >
        <Heading
          as="h1"
          fontSize={{ base: "2xl", md: "4xl" }} // Responsive font sizes
          mb={4}
          color={colorMode === "light" ? "gray.900" : "white"}
        >
          My Graphic Design Skills
        </Heading>
        <Text
          fontSize={{ base: "sm", md: "lg" }} // Responsive font sizes
          mb={8}
          color={colorMode === "light" ? "gray.600" : "gray.400"}
        >
          As a graphics designer, I've acquired a range of skills that enable me
          to create visually appealing designs. From logos to brochures, I've
          got you covered.
        </Text>

        <Box w="full" overflow="hidden">
          {" "}
          {/* Add overflow: hidden */}
          <Carousel
            ref={carouselRef}
            responsive={responsive}
            containerClass="carousel-container"
            additionalTransfrom={0}
            arrows={true} // Enable arrows for navigation
            itemClass="carousel-item"
            slidesToSlide={1}
            swipeable={true}
            draggable={true}
            focusOnSelect={true}
            infinite={true} // Ensure infinite looping
            width="100%" // Set width to 100%
            padding={4} // Adjust padding
          >
            {skills.map((skill) => (
              <Box
                key={skill.name}
                mx={1} // Margin for spacing
                display="flex"
                justifyContent="center"
              >
                <Card
                  bg={colorMode === "light" ? "gray.200" : "gray.800"}
                  color={colorMode === "light" ? "gray.600" : "gray.400"}
                  p={4}
                  border=" 1px solid"
                  borderColor={colorMode === "light" ? "gray.300" : "gray.600"}
                  borderRadius="md"
                  boxShadow="0px 10px 20px rgba(0, 0, 0, 0.1)"
                >
                  <CardHeader>
                    <Icon
                      as={FontAwesomeIcon}
                      icon={skill.icon}
                      fontSize="2xl"
                      color={colorMode === "light" ? "gray.600" : "gray.400"}
                    />
                  </CardHeader>
                  <CardBody>
                    <Heading
                      as="h2"
                      fontSize={{ base: "lg", md: "xl" }} // Responsive font sizes
                      mb={2}
                      color={colorMode === "light" ? "gray.900" : "white"}
                    >
                      {skill.name}
                    </Heading>
                    <Text
                      fontSize={{ base: "sm", md: "md" }} // Responsive font sizes
                      color={colorMode === "light" ? "gray.600" : "gray.400"}
                    >
                      {skill.description}
                    </Text>
                  </CardBody>
                </Card>
              </Box>
            ))}
          </Carousel>
        </Box>

        <Box
          display="flex"
          justifyContent="space-between"
          alignItems="center"
          mt={4}
        >
          <Icon
            as={FontAwesomeIcon}
            icon={["fas", "chevron-left"]}
            fontSize="2xl"
            color={colorMode === "light" ? "gray.600" : "gray.400"}
            cursor="pointer"
            onClick={() => handleArrowClick("prev")}
          />
          <Icon
            as={FontAwesomeIcon}
            icon={["fas", "chevron-right"]}
            fontSize="2xl"
            color={colorMode === "light" ? "gray.600" : "gray.400"}
            cursor="pointer"
            onClick={() => handleArrowClick("next")}
          />
        </Box>
      </Container>
    </Box>
  );
}
