import { CodeIcon } from "@heroicons/react/solid";
import React from "react";
import { projects } from "../data";
import {
  Box,
  Container,
  Grid,
  GridItem,
  Heading,
  Text,
  Link,
  Image,
  useColorMode,
} from "@chakra-ui/react";

export default function Projects() {
  const { colorMode } = useColorMode();

  return (
    <Box
      id="projects"
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={10}
    >
      <Container
        maxW="container.xl"
        mx="auto"
        px={{ base: 4, md: 10 }}
        textAlign="center"
      >
        <Grid justify="center" align="center" mb={8}>
          <Heading
            as="h1"
            fontSize={{ base: "2xl", md: "3xl", lg: "4xl" }}
            mb={4}
            color={colorMode === "light" ? "gray.900" : "white"}
          >
            Graphics Design
          </Heading>
          <Text
            fontSize={{ base: "md", md: "lg" }}
            mb={8}
            color={colorMode === "light" ? "gray.600" : "gray.400"}
          >
            As a graphics designer, I've created a variety of projects that
            showcase my skills in visual design and creativity. From logos to
            brochures, I've got you covered.
          </Text>
        </Grid>

        <Grid templateColumns={{ base: "1fr", md: "repeat(3, 1fr)" }} gap={8}>
          {projects.map((project) => (
            <GridItem key={project.title}>
              <Box
                bg={colorMode === "light" ? "gray.200" : "gray.800"}
                color={colorMode === "light" ? "gray.600" : "gray.400"}
                p={4}
                borderBottom="4px solid" // Set bottom border only
                borderBottomColor="green.500" // Green bottom border
                borderRadius="lg"
                h="300px" // Fixed height for the project box
                w={{ base: "100%", md: "400px" }} // Responsive width
                mx="auto" // Centering the box
              >
                <Box position="relative" overflow="hidden" borderRadius="lg">
                  <Image
                    src={project.image}
                    alt={project.title} // Ensure this is descriptive
                    w="full"
                    h={{ base: "150px", md: "200px" }}
                    objectFit="cover"
                    objectPosition="center"
                    borderRadius="lg"
                  />
                  <Box
                    position="absolute"
                    top="0"
                    left="0"
                    w="full"
                    h="full"
                    bg={colorMode === "light" ? "gray.100" : "gray.900"}
                    opacity="0"
                    transition="opacity 0.3s ease"
                    _hover={{ opacity: 0.8 }} // Optional hover effect for the overlay
                    display="flex"
                    flexDirection="column"
                    justifyContent="center"
                    alignItems="center"
                    borderRadius="lg"
                  >
                    <Heading
                      as="h2"
                      fontSize={{ base: "md", md: "lg" }}
                      mb={2}
                      color={colorMode === "light" ? "gray.900" : "white"}
                    >
                      {project.title}
                    </Heading>
                    <Text
                      fontSize={{ base: "sm", md: "md" }}
                      mb={2}
                      color={colorMode === "light" ? "gray.600" : "gray.400"}
                    >
                      {project.subtitle}
                    </Text>
                    <Link
                      href={project.link} // Assuming there's a link in your project data
                      isExternal
                      color={colorMode === "light" ? "green.500" : "green.300"}
                      fontWeight="bold"
                    >
                      View Project
                    </Link>
                  </Box>
                </Box>
              </Box>
            </GridItem>
          ))}
        </Grid>
      </Container>
    </Box>
  );
}
