import {
  Box,
  Container,
  Flex,
  Heading,
  Text,
  Button,
  Image,
  Link,
  useColorMode,
  IconButton,
} from "@chakra-ui/react";
import { SunIcon, MoonIcon } from "@chakra-ui/icons"; // Import icons for brightness control
import React from "react";
import { motion } from "framer-motion";
import ezedinImage from "../photo.png"; // Adjust the path as necessary

function About() {
  const { colorMode, toggleColorMode } = useColorMode();

  return (
    <Box
      id="about"
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={10}
      h="85vh"
      bg={colorMode === "light" ? "gray.100" : "gray.900"} // added background color
    >
      <Container maxW="container.xl" mx="auto" px={{ base: 4, md: 10 }}>
        <Flex flexDirection="row" align="center" justify="space-between">
          <Box flexBasis={{ base: "50%", md: "50%" }}>
            <Heading
              as="h1"
              fontSize={{ base: "2xl", md: "3xl", lg: "4xl" }}
              mb={4}
            >
              <motion.div
                initial={{ opacity: 0, y: 20, scale: 0.5 }}
                animate={{ opacity: 1, y: 0, scale: 1 }}
                transition={{ duration: 0.8 }}
              >
                Hi, I'm Ezedin.
              </motion.div>
              <br />
              <motion.div
                initial={{ opacity: 0 }}
                animate={{
                  opacity: 1,
                }}
                transition={{
                  duration: 0.8,
                  delay: 0.3,
                }}
              >
                <Text
                  as="span"
                  variant="typing"
                  fontSize={{ base: "2xl", md: "3xl", lg: "4xl" }}
                >
                  I'm a graphics designer.
                </Text>
              </motion.div>
            </Heading>
            <Text
              fontSize={{ base: "md", md: "lg" }}
              mb={8}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.6 }}
            >
              As a graphics designer, I've worked with a range of design tools,
              from Adobe Creative Suite to Sketch and Figma. I'm passionate
              about creating visually appealing designs and crafting unique
              brand identities.
            </Text>
            <Flex justify="space-between" align="center" gap={4}>
              <Button
                as={Link}
                href="#contact"
                variant="solid"
                colorScheme="green"
                size={{ base: "sm", md: "md" }}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.9 }}
              >
                Let's build something together
              </Button>
              <Button
                as={Link}
                href="#projects"
                variant="outline"
                colorScheme="gray"
                size={{ base: "sm", md: "md" }}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 1.2 }}
              >
                Check out my past projects
              </Button>
            </Flex>
          </Box>
          <Box flexBasis={{ base: "50%", md: "50%" }}>
            <motion.div
              initial={{ opacity: 0, x: -100 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 1.5 }}
            >
              <Image
                src={ezedinImage}
                objectFit="cover"
                objectPosition="center"
                rounded="lg"
                minWidth="780px"
                width={{ base: "80%", md: "80%", lg: "80%" }}
              />
            </motion.div>
          </Box>
        </Flex>
      </Container>
    </Box>
  );
}

export default About;
