import {
  Box,
  Container,
  Flex,
  Heading,
  Text,
  Button,
  Image,
  Link,
  useColorMode,
} from "@chakra-ui/react";
import React from "react";
import { motion } from "framer-motion";
import ezedinImage from "../photo.png"; // Adjust the path as necessary

function About() {
  const { colorMode } = useColorMode();

  return (
    <Box
      id="about"
      bg={`url(${ezedinImage})`} // Use the image as a background
      bgSize="100vw auto" // Make the image proportional to the height
      bgPosition="center" // Center the image
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={10}
      h="85vh" // Add a height of 100vh to the component
    >
      <Container maxW="container.xl" mx="auto" px={{ base: 4, md: 10 }}>
        <Flex justify="space-between" align="center" flexWrap="wrap">
          <Box flexBasis={{ base: "100%", md: "40%" }} pr={{ md: 20 }}>
            <Heading
              as="h1"
              fontSize={{ base: "2xl", md: "3xl", lg: "4xl" }}
              mb={4}
            >
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8 }}
              >
                Hi, I'm Ezedin.
              </motion.div>
              <br />
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.3 }}
              >
                I'm a graphics designer.
              </motion.div>
            </Heading>
            <Text fontSize={{ base: "md", md: "lg" }} mb={8}>
              As a graphics designer, I've worked with a range of design tools,
              from Adobe Creative Suite to Sketch and Figma. I'm passionate
              about creating visually appealing designs and crafting unique
              brand identities.
            </Text>
            <Flex justify="space-between" align="center" gap={4}>
              <Button
                as={Link}
                href="#contact"
                variant="solid"
                colorScheme="green"
                size={{ base: "sm", md: "md" }}
              >
                Let's build something together
              </Button>
              <Button
                as={Link}
                href="#projects"
                variant="outline"
                colorScheme="gray"
                size={{ base: "sm", md: "md" }}
              >
                Check out my past projects
              </Button>
            </Flex>
          </Box>
          {/* <Box flexBasis={{ base: "100%", md: "47%" }} pl={{ md: 20 }}>
            <Image
              src={ezedinImage}
              objectFit="cover"
              objectPosition="center"
              rounded="lg"
              minWidth="400px"
              width={{ base: "80%", md: "80%", lg: "80%" }}
            />
          </Box> */}
        </Flex>
      </Container>
    </Box>
  );
}

export default About;
