import { CodeIcon } from "@heroicons/react/solid";
import React from "react";
import { projects } from "../data"; // Make sure this data has valid image URLs
import {
  Box,
  Container,
  Grid,
  GridItem,
  Heading,
  Text,
  Image,
  useColorMode,
} from "@chakra-ui/react";

export default function Projects() {
  const { colorMode } = useColorMode();

  return (
    <Box
      id="projects"
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={20}
    >
      <Container
        maxW="container.xl"
        mx="auto"
        px={{ base: 4, md: 10 }}
        textAlign="center"
      >
        <Grid justify="center" align="center" mb={8}>
          <CodeIcon className="inline-block w-6 h-6 mb-4" />
          <Heading
            as="h1"
            fontSize={{ base: "2xl", md: "3xl", lg: "4xl" }}
            mb={4}
            color={colorMode === "light" ? "gray.900" : "white"}
          >
            Graphics Design
          </Heading>
          <Text
            fontSize={{ base: "md", md: "lg" }}
            mb={8}
            color={colorMode === "light" ? "gray.600" : "gray.400"}
          >
            As a graphics designer, I've created a variety of projects that
            showcase my skills in visual design and creativity. From logos to
            brochures, I've got you covered.
          </Text>
        </Grid>
        <Grid
          templateColumns={{ base: "repeat(1, 1fr)", md: "repeat(3, 1fr)" }}
          gap={8}
        >
          {projects.map((project) => (
            <GridItem key={project.title}>
              <Box
                bg={colorMode === "light" ? "gray.200" : "gray.800"}
                color={colorMode === "light" ? "gray.600" : "gray.400"}
                p={4}
                border="1px solid"
                borderColor={colorMode === "light" ? "gray.200" : "gray.800"}
                borderRadius="lg"
                boxShadow="lg"
                position="relative" // Ensure positioning for overlay
                overflow="hidden" // Prevent overflow
                w="full"
                h={{ base: "150px", md: "200px" }}
              >
                <Image
                  src={project.image || "https://via.placeholder.com/150"} // Placeholder if image is missing
                  alt={project.title}
                  borderRadius="lg"
                  objectFit="cover"
                  boxSize="100%" // Fill the Box
                />
                <Box
                  bg={colorMode === "light" ? "gray.300" : "gray.700"}
                  w="full"
                  h="full"
                  borderRadius="lg"
                  position="absolute" // Position overlay absolutely
                  top={0}
                  left={0}
                  display="flex"
                  flexDirection="column"
                  justifyContent="center"
                  alignItems="center"
                  opacity={0.9} // Slightly transparent for overlay effect
                >
                  <Text fontSize="lg" fontWeight="bold">
                    {project.title}
                  </Text>
                  <Text fontSize="md" color="gray.600">
                    {project.subtitle}
                  </Text>
                </Box>
              </Box>
            </GridItem>
          ))}
        </Grid>
      </Container>
    </Box>
  );
}
