import {
  Box,
  Flex,
  Link,
  Button,
  IconButton,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  useColorMode,
  useMediaQuery,
  Circle,
  Image,
} from "@chakra-ui/react";
import { HamburgerIcon, CloseIcon } from "@chakra-ui/icons";
import { useState, useEffect } from "react";

const Navbar = () => {
  const { colorMode } = useColorMode();
  const [isOpen, setIsOpen] = useState(false);
  const [isLargerThan768] = useMediaQuery("(min-width: 768px)");
  const [isScrolled, setIsScrolled] = useState(false);

  const toggleMenu = () => setIsOpen(!isOpen);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <Flex
      justify="space-between"
      align="center"
      flexWrap="wrap"
      px={{ base: 4, md: 10 }}
    >
      <Box
        bg={colorMode === "light" ? "gray.100" : "gray.900"}
        color={colorMode === "light" ? "gray.800" : "gray.400"}
        pt={4}
        pb={4}
        position={isScrolled ? "fixed" : "relative"}
        top={isScrolled ? 0 : undefined}
        zIndex={10}
      >
        <Box>
          <Link href="#about" target="_blank" rel="noopener noreferrer">
            <Image src={require("../logo.png")} alt="Logo" width="80px" />
          </Link>
        </Box>
        {isLargerThan768 ? (
          <Flex justify="space-between" align="center">
            <Link
              href="#projects"
              fontSize="lg"
              color="gray.400"
              _hover={{ color: "white" }}
              mr={5}
              target="_blank"
              rel="noopener noreferrer"
            >
              Past Work
            </Link>
            <Link
              href="#testimonials"
              fontSize="lg"
              color="gray.400"
              _hover={{ color: "white" }}
              mr={5}
              target="_blank"
              rel="noopener noreferrer"
            >
              Testimonials
            </Link>
            <Button
              as={Link}
              href="#contact"
              variant="solid"
              colorScheme="green"
              size="lg"
              target="_blank"
              rel="noopener noreferrer"
            >
              Hire Me
            </Button>
          </Flex>
        ) : (
          <Menu isOpen={isOpen}>
            <MenuButton
              as={IconButton}
              aria-label="Open menu"
              icon={isOpen ? <CloseIcon /> : <HamburgerIcon />}
              onClick={toggleMenu}
            />
            <MenuList>
              <MenuItem
                as={Link}
                href="#projects"
                target="_blank"
                rel="noopener noreferrer"
              >
                Past Work
              </MenuItem>
              <MenuItem
                as={Link}
                href="#testimonials"
                target="_blank"
                rel="noopener noreferrer"
              >
                Testimonials
              </MenuItem>
              <MenuItem
                as={Link}
                href="#contact"
                target="_blank"
                rel="noopener noreferrer"
              >
                Hire Me
              </MenuItem>
            </MenuList>
          </Menu>
        )}
      </Box>
    </Flex>
  );
};

export default Navbar;
