import { BadgeCheckIcon, ChipIcon } from "@heroicons/react/solid";
import React, { useRef, useState, useEffect } from "react";
import { skills } from "../data"; // Ensure this includes the icon data
import {
  Box,
  Container,
  Flex,
  Heading,
  Text,
  Icon,
  useColorMode,
  Card,
  CardBody,
  CardHeader,
  Button,
} from "@chakra-ui/react";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css"; // Import Carousel styles
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"; // Import FontAwesomeIcon

export default function Skills() {
  const { colorMode } = useColorMode();
  const carouselRef = useRef(null); // Create a ref for the carousel
  const [currentIndex, setCurrentIndex] = useState(0);

  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5,
      partialVisibilityGutter: 40,
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
      partialVisibilityGutter: 40,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
      partialVisibilityGutter: 40,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
      partialVisibilityGutter: 40,
    },
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex((currentIndex + 1) % skills.length);
      carouselRef.current.goToSlide(currentIndex, 500); // Add a small speed of 500ms
    }, 5000); // Change slide every 5 seconds
    return () => clearInterval(intervalId);
  }, [currentIndex, skills]);

  const handleArrowClick = (direction) => {
    if (direction === "next") {
      setCurrentIndex((currentIndex + 1) % skills.length);
      carouselRef.current.next(500); // Add a small speed of 500ms
    } else {
      setCurrentIndex((currentIndex - 1 + skills.length) % skills.length);
      carouselRef.current.previous(500); // Add a small speed of 500ms
    }
  };

  return (
    <Box
      id="skills"
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={20}
      borderBottom="1px solid"
      borderBottomColor={colorMode === "light" ? "gray.200" : "gray.700"}
      boxShadow="0px 10px 20px rgba(0, 0, 0, 0.1)"
    >
      <Container
        maxW="container.xl"
        mx="auto"
        px={{ base: 4, md: 10 }}
        textAlign="center"
      >
        <Flex justify="center" align="center" flexWrap="wrap">
          <Heading
            as="h1"
            fontSize={{ base: "3xl", md: "4xl" }}
            mb={4}
            color={colorMode === "light" ? "gray.900" : "white"}
          >
            My Graphic Design Skills
          </Heading>
          <Text
            fontSize="lg"
            mb={8}
            color={colorMode === "light" ? "gray.600" : "gray.400"}
          >
            As a graphics designer, I've acquired a range of skills that enable
            me to create visually appealing designs. From logos to brochures,
            I've got you covered.
          </Text>
        </Flex>
        <Box w="full" overflow="hidden">
          <Carousel
            ref={carouselRef}
            responsive={responsive}
            containerClass="carousel-container"
            additionalTransfrom={-20}
            arrows={false}
            itemClass="carousel-item"
            slidesToSlide={1}
            swipeable={true}
            draggable={true}
            focusOnSelect={true}
          >
            {skills.map((skill) => (
              <Card
                key={skill.name}
                bg={colorMode === "light" ? "gray.200" : "gray.800"}
                color={colorMode === "light" ? "gray.600" : "gray.400"}
                p={4}
                border="1px solid"
                borderColor={colorMode === "light" ? "gray.200" : "gray.800"}
                borderRadius="lg"
                mx={2}
                borderBottom="4px solid" // Set bottom border only
                borderBottomColor="green.500" // Bottom border color
                _hover={{
                  bg: colorMode === "light" ? "gray.300" : "gray.700",
                  color: "white",
                  transform: "scale(1.05)",
                  transition: "all 0.3s ease-in-out",
                }}
              >
                <CardHeader>
                  <Icon
                    w={12}
                    h={12}
                    as={FontAwesomeIcon}
                    icon={skill.icon}
                    className="w-6 h-6 flex-shrink-0 mr-4"
                  />
                </CardHeader>
                <CardBody>
                  <Text fontSize="lg" fontWeight="bold">
                    {skill.name}
                  </Text>
                </CardBody>
              </Card>
            ))}
          </Carousel>
        </Box>

        {/* Arrows below the carousel */}
        <Flex justify="center" mt={4}>
          <Button
            bg={colorMode === "light" ? "gray.200" : "gray.800"}
            color={colorMode === "light" ? "gray.600" : "gray.400"}
            _hover={{
              bg: colorMode === "light" ? "gray.300" : "gray.700",
            }}
            onClick={() => handleArrowClick("prev")}
            mr={2}
          >
            {"<"}
          </Button>
          <Button
            bg={colorMode === "light" ? "gray.200" : "gray.800"}
            color={colorMode === "light" ? "gray.600" : "gray.400"}
            _hover={{
              bg: colorMode === "light" ? "gray.300" : "gray.700",
            }}
            onClick={() => handleArrowClick("next")}
          >
            {">"}
          </Button>
        </Flex>
      </Container>
    </Box>
  );
}
