import { CodeIcon } from "@heroicons/react/solid";
import React from "react";
import { projects } from "../data";
import {
  Box,
  Container,
  Grid,
  GridItem,
  Heading,
  Text,
  Link,
  Image,
  useColorMode,
} from "@chakra-ui/react";

export default function Projects() {
  const { colorMode } = useColorMode();

  return (
    <Box
      id="projects"
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={10}
    >
      <Container maxW="container.xl" mx="auto" px={{ base: 4, md: 8, lg: 12 }}>
        <Heading
          as="h2"
          fontSize={{ base: "xl", md: "2xl" }}
          mb={4}
          textAlign="center"
        >
          Projects
        </Heading>
        <Grid
          templateColumns={{
            base: "repeat(1, 1fr)",
            md: "repeat(2, 1fr)",
            lg: "repeat(3, 1fr)",
          }}
          gap={4}
        >
          {projects.map((project) => (
            <GridItem key={project.title}>
              <Box
                bg={colorMode === "light" ? "gray.200" : "gray.800"}
                color={colorMode === "light" ? "gray.600" : "gray.400"}
                p={4}
                border="1px solid"
                borderColor={colorMode === "light" ? "gray.200" : "gray.800"}
                borderRadius="lg"
                borderTopWidth="4px"
                borderTopColor={
                  colorMode === "light" ? "green.500" : "green.300"
                }
                borderTopRadius="lg"
                boxShadow="lg"
                transition="transform 0.3s ease"
                _hover={{ transform: "translateY(-5px)", boxShadow: "xl" }}
                w={{ base: "100%", md: "400px" }} // Responsive width
                mx="auto" // Centering the box
              >
                <Image
                  src={project.image}
                  alt={project.title}
                  w="full"
                  h={48}
                  objectFit="cover"
                  borderRadius="lg"
                />
                <Heading as="h3" fontSize={{ base: "lg", md: "xl" }} mb={2}>
                  {project.title}
                </Heading>
                <Text fontSize={{ base: "sm", md: "md" }} mb={4}>
                  {project.description}
                </Text>
                <Link
                  href={project.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  fontSize={{ base: "sm", md: "md" }}
                  color={colorMode === "light" ? "blue.500" : "blue.300"}
                  _hover={{ textDecoration: "underline" }}
                >
                  Visit Project <CodeIcon className="h-4 w-4 inline" />
                </Link>
              </Box>
            </GridItem>
          ))}
        </Grid>
      </Container>
    </Box>
  );
}
