import React from "react";
import { Box, Container, Heading, Text, useColorMode } from "@chakra-ui/react";

const Aboutme = () => {
  const { colorMode } = useColorMode();

  return (
    <Box
      id="about"
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={20}
      borderBottom="1px solid"
      borderBottomColor={colorMode === "light" ? "gray.200" : "gray.700"}
      boxShadow="0px 10px 20px rgba(0, 0, 0, 0.1)"
    >
      <Container
        maxW="container.xl"
        mx="auto"
        px={{ base: 4, md: 10 }}
        textAlign="center"
      >
        <Heading
          as="h1"
          fontSize={{ base: "2xl", md: "3xl", lg: "4xl" }}
          mb={4}
          color={colorMode === "light" ? "gray.900" : "white"}
        >
          About Me
        </Heading>
        <Text
          fontSize={{ base: "md", md: "lg" }}
          mb={8}
          color={colorMode === "light" ? "gray.600" : "gray.400"}
        >
          I am a beginner graphic designer who enjoys freelancing. My skills
          have been developed through online tutorials, and I am currently a
          4th-year software engineering student at Jimma University. I am
          excited to blend my design and technical skills to create impactful
          projects.
        </Text>
      </Container>
    </Box>
  );
};

export default AboutMe;
