import React from "react";
import { PencilIcon, ChatIcon } from "@heroicons/react/solid"; // Using PencilIcon and ChatIcon
import { testimonials } from "../data";
import {
  Box,
  Container,
  Flex,
  Heading,
  Text,
  Image,
  Icon,
  useColorMode,
  Grid,
  GridItem,
} from "@chakra-ui/react";

export default function Testimonials() {
  const { colorMode } = useColorMode();

  return (
    <Box
      id="testimonials"
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={20}
    >
      <Container
        maxW="container.xl"
        mx="auto"
        px={{ base: 4, md: 10 }}
        textAlign="center"
      >
        <Flex justify="center" align="center" mb={8}>
          <Icon
            as={PencilIcon} // Keep the updated icon
            className="mx-auto inline-block w-6 h-6 mb-4"
            color={colorMode === "light" ? "gray.900" : "white"}
          />
          <Heading
            as="h1"
            fontSize={{ base: "2xl", md: "3xl", lg: "4xl" }}
            mb={4}
            color={colorMode === "light" ? "gray.900" : "white"}
          >
            What My Clients Say About My Graphic Design Services
          </Heading>
          <Text
            fontSize={{ base: "md", md: "lg" }}
            mb={8}
            color={colorMode === "light" ? "gray.600" : "gray.400"}
          >
            As a graphic designer, I've had the pleasure of working with a
            variety of clients across different industries. Here's what they
            have to say about my services.
          </Text>
        </Flex>

        <Grid templateColumns={{ base: "1fr", md: "1fr 1fr" }} gap={6}>
          {testimonials.map((testimonial) => (
            <GridItem
              key={testimonial.name}
              bg={colorMode === "light" ? "gray.200" : "gray.800"}
              color={colorMode === "light" ? "gray.600" : "gray.400"}
              p={6}
              border="1px solid"
              borderColor={colorMode === "light" ? "gray.200" : "gray.800"}
              borderRadius="lg"
              position="relative" // Needed for the shadow effect
              _before={{
                content: '""',
                position: "absolute",
                top: "-4px", // Adjust position for the top shadow
                left: 0,
                right: 0,
                height: "4px",
                bg: colorMode === "light" ? "gray.200" : "gray.800", // Match the border color
                borderRadius: "lg",
                boxShadow:
                  colorMode === "light"
                    ? "0 2px 4px rgba(0,0,0,0.2)"
                    : "0 2px 4px rgba(255,255,255,0.2)",
              }}
              boxShadow="none" // Remove default box shadow
              borderBottom="4px solid" // Add bottom border
              borderBottomColor={
                colorMode === "light" ? "green.500" : "green.300"
              } // Color for the bottom border
            >
              <Icon
                as={ChatIcon} // Use the updated icon
                className="block w-6 h-6 text-gray-500 mb-4"
              />
              <Text
                fontSize={{ base: "md", md: "lg" }}
                color={colorMode === "light" ? "gray.900" : "white"}
                mb={4}
              >
                {testimonial.quote}
              </Text>
              <Flex align="center" mt={4}>
                <Image
                  src={testimonial.image}
                  alt={testimonial.name} // Ensure alt text is descriptive
                  w={12}
                  h={12}
                  borderRadius="full"
                  objectFit="cover"
                  objectPosition="center"
                />
                <Box ml={4}>
                  <Text
                    fontSize={{ base: "md", md: "lg" }}
                    color={colorMode === "light" ? "gray.900" : "white"}
                  >
                    {testimonial.name}
                  </Text>
                  <Text
                    fontSize={{ base: "sm", md: "md" }}
                    color={colorMode === "light" ? "gray.600" : "gray.500"}
                  >
                    {testimonial.company}
                  </Text>
                </Box>
              </Flex>
            </GridItem>
          ))}
        </Grid>
      </Container>
    </Box>
  );
}
