import React from "react";
import {
  TerminalIcon,
  UsersIcon,
  BrushIcon,
  QuoteIcon,
} from "@heroicons/react/solid";
import { projects } from "../data";
import {
  Box,
  Container,
  Flex,
  Heading,
  Text,
  Image,
  Badge,
  Icon,
  useColorMode,
  Grid,
  GridItem,
} from "@chakra-ui/react";

export default function Projects() {
  const { colorMode } = useColorMode();

  return (
    <Box
      id="projects"
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={20}
    >
      <Container
        maxW="container.xl"
        mx="auto"
        px={{ base: 4, md: 10 }}
        textAlign="center"
      >
        <Flex justify="center" align="center" mb={8}>
          <Icon
            as={BrushIcon}
            className="mx-auto inline-block w-6 h-6 mb-4"
            color={colorMode === "light" ? "gray.900" : "white"}
          />
          <Heading
            as="h1"
            fontSize={{ base: "2xl", md: "3xl", lg: "4xl" }}
            mb={4}
            color={colorMode === "light" ? "gray.900" : "white"}
          >
            My Graphic Design Projects
          </Heading>
          <Text
            fontSize={{ base: "md", md: "lg" }}
            mb={8}
            color={colorMode === "light" ? "gray.600" : "gray.400"}
          >
            As a graphic designer, I've had the pleasure of working on a variety
            of projects across different industries. Here are some of my recent
            projects.
          </Text>
        </Flex>
        <Grid templateColumns={{ base: "1fr", md: "1fr 1fr" }} gap={6}>
          {projects.map((project) => (
            <GridItem
              key={project.title}
              bg={colorMode === "light" ? "gray.200" : "gray.800"}
              color={colorMode === "light" ? "gray.600" : "gray.400"}
              p={6}
              border="1px solid"
              borderColor={colorMode === "light" ? "gray.200" : "gray.800"}
              borderRadius="lg"
              boxShadow="lg"
            >
              <Image
                src={project.image}
                alt={project.title}
                w="full"
                h="full"
                borderRadius="lg"
                objectFit="cover"
                objectPosition="center"
              />
              <Box p={4}>
                <Text
                  fontSize={{ base: "md", md: "lg" }}
                  color={colorMode === "light" ? "gray.900" : "white"}
                  mb={4}
                >
                  {project.title}
                </Text>
                <Text
                  fontSize={{ base: "sm", md: "md" }}
                  color={colorMode === "light" ? "gray.600" : "gray.500"}
                >
                  {project.description}
                </Text>
              </Box>
            </GridItem>
          ))}
        </Grid>
      </Container>
    </Box>
  );
}
