import { BadgeCheckIcon, ChipIcon } from "@heroicons/react/solid";
import React, { useRef } from "react";
import { skills } from "../data"; // Update this to include icon data
import {
  Box,
  Container,
  Flex,
  Heading,
  Text,
  Icon,
  useColorMode,
  Card,
  CardBody,
  CardHeader,
  Button,
} from "@chakra-ui/react";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css"; // Ensure you import the CSS styles

export default function Skills() {
  const { colorMode } = useColorMode();
  const carouselRef = useRef(null); // Create a ref for the carousel

  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5,
      partialVisibilityGutter: 40,
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
      partialVisibilityGutter: 40,
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
      partialVisibilityGutter: 40,
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
      partialVisibilityGutter: 40,
    },
  };

  return (
    <Box
      id="skills"
      bg={colorMode === "light" ? "gray.100" : "gray.900"}
      color={colorMode === "light" ? "gray.800" : "gray.400"}
      py={20}
    >
      <Container maxW="container.xl" mx="auto" px={10} textAlign="center">
        <Flex justify="center" align="center" flexWrap="wrap">
          <Icon
            as={ChipIcon}
            className="inline-block w-12 mb-4"
            height={100}
            color={colorMode === "light" ? "gray.900" : "white"}
          />
          <Heading
            as="h1"
            fontSize={{ base: "3xl", md: "4xl" }}
            mb={4}
            color={colorMode === "light" ? "gray.900" : "white"}
          >
            Graphics Design Skills
          </Heading>
          <Text
            fontSize="lg"
            mb={8}
            color={colorMode === "light" ? "gray.600" : "gray.400"}
          >
            As a graphics designer, I've acquired a range of skills that enable
            me to create visually appealing designs. From logos to brochures,
            I've got you covered.
          </Text>
        </Flex>
        <Carousel
          ref={carouselRef} // Attach the ref to the carousel
          responsive={responsive}
          containerProps={{
            px: 4, // add a margin to the carousel container
          }}
          additionalTransfrom={-20}
          arrows={false} // Disable default arrows
          itemClass="carousel-item"
          slidesToSlide={1} // slide one card at a time
          swipeable={true} // enable swipe gesture
          draggable={true} // enable drag gesture
          focusOnSelect={true} // focus on selected card
        >
          {skills.map((skill) => (
            <Card
              key={skill.name}
              bg={colorMode === "light" ? "gray.200" : "gray.800"}
              color={colorMode === "light" ? "gray.600" : "gray.400"}
              p={4}
              border="1px solid"
              borderColor={colorMode === "light" ? "gray.200" : "gray.800"}
              borderRadius="lg"
              boxShadow="lg"
              _hover={{
                bg: colorMode === "light" ? "gray.300" : "gray.700",
                color: "white",
                transform: "scale(1.05)",
                transition: "all 0.3s ease-in-out",
              }}
              mx={2} // add margin to the card for spacing
            >
              <CardHeader>
                <Icon
                  as={BadgeCheckIcon}
                  className="text-green-400 w-6 h-6 flex-shrink-0 mr-4"
                />
                <Icon as={skill.icon} className="w-6 h-6 flex-shrink-0 mr-4" />
              </CardHeader>
              <CardBody>
                <Text fontSize="lg" fontWeight="bold">
                  {skill.name}
                </Text>
              </CardBody>
            </Card>
          ))}
        </Carousel>

        {/* Arrows below the carousel */}
        <Flex justify="center" mt={4}>
          <Button
            bg={colorMode === "light" ? "gray.200" : "gray.800"}
            color={colorMode === "light" ? "gray.600" : "gray.400"}
            _hover={{
              bg: colorMode === "light" ? "gray.300" : "gray.700",
            }}
            onClick={() => carouselRef.current.previous()} // Slide left
            mr={2} // Margin between buttons
          >
            {"<"}
          </Button>
          <Button
            bg={colorMode === "light" ? "gray.200" : "gray.800"}
            color={colorMode === "light" ? "gray.600" : "gray.400"}
            _hover={{
              bg: colorMode === "light" ? "gray.300" : "gray.700",
            }}
            onClick={() => carouselRef.current.next()} // Slide right
          >
            {">"}
          </Button>
        </Flex>
      </Container>
    </Box>
  );
}
